<script>
  import events from "../events"

  export default {
    name: "halfWavePrince",
    data() {
      return {
        events,
        deptEvents: [],
        event: null,
        t2: null
      }
    },
    methods: {
      showDetails(event) {
        this.event = event
        this.t2.play()
      }
    },
    created() {
      let self = this
      this.events.forEach(function(event) {
        if(event.department === "ecel")
          self.deptEvents.push(event)
      })
    },
    mounted() {
      let t1 = new this.$gsap.TimelineMax()
      t1.to('.left-side .front, .left-side .back', 4, {
        rotationY: 180,
        transformOrigin: 'left top'
      })
      t1.from('.left-side .back svg', 0.5, {
        autoAlpha: 0
      }, "-=1.5")
      t1.to('.left-side .front', 0.5, {
        autoAlpha: 0
      }, "-=3")

      this.t2 = new this.$gsap.TimelineMax({
        paused: true
      })
      this.t2.to('#right-side', 1, {
        rotationY: 180,
        transformOrigin: 'left top',
        autoAlpha: 0
      })
      this.t2.from('.page-2-left', 0.1, {
        autoAlpha: 0
      }, "-=0.6")
      this.t2.from('.page-2-right', 0.1, {
        autoAlpha: 0
      }, "-=1")

    }
  }
</script>

